#!/bin/bash
systemctl enable rpcbind
systemctl start rpcbind
systemctl enable iscsid
systemctl start iscsid
lsmod | greg iscsi
echo YmxhY2tsaXN0IHsKCWRldmljZSB7CgkJdmVuZG9yICJfSEVEVklHXyIKCX0KfQo= | base64 -d > /etc/multipath.conf
echo "created /etc/multipath.conf"
systemctl enable messagebus
systemctl start messagebus
echo  | base64 -d > /etc/dbus-1/system.d/org.ganesha.nfsd.conf
systemctl restart messagebus
echo "created /etc/dbus-1/system.d/org.ganesha.nfsd.conf"
echo  | base64 -d > /etc/systemd/system/metacache.service
systemctl enable metacache
systemctl start metacache
echo "created and started /etc/systemd/system/metacache.service"
df -hk | grep /hedvig/cache
